/* Custom Css*/

.halo-ask-an-expert {
    display: -ms-flexbox;
    display: flex;
    -ms-flex-align: center;
    align-items: center;
    -ms-flex-pack: space-between;
    justify-content: space-between;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
    width: 100%;
    max-width: 570px;
    margin: 0 auto;
    background: #f5f5f5;
    border-radius: 100px;
    padding: 25px 25px;
}


.halo-ask-an-expert .col-right .ask-an-expert-link {
    text-transform: uppercase;
    font-weight: 700;
    background: #f6b924;
    border-color: #f6b924;
    color: #202020;
}

.halo-ask-an-expert .col-right {
    width: 160px;
}
.product-bottom-wrapper .halo-ask-an-expert .col-left {
    display: -ms-flexbox;
    display: flex;
    -ms-flex-align: center;
    align-items: center;
    -ms-flex-pack: space-between;
    justify-content: space-between;
    -ms-flex-wrap: wrap;
    -webkit-flex-wrap: wrap;
    width: calc(100% - 160px);
    padding-right: 15px;
}

.halo-ask-an-expert .col-left .text h3 {
    margin-top: 0;
    margin-bottom: 5px;
    font-size: 16px;
    line-height: 24px;
    text-transform: uppercase;
    font-style: italic;
}

 .halo-ask-an-expert .col-left .text {
    width: calc(100% - 89px);
    padding-left: 18px;
    display: inline-block;
}

