.Moveable {
  .multiStep {
      position: relative;
      overflow: hidden;
      margin-bottom: 15px;
      margin-top: 25px;
      border: 0;
      &:after {
          position: absolute;
          bottom: 0;
          width: 100%;
          z-index: 2;
          height: 3px;
          left: 0;
          content: '';
          background: #8200ff;
          @media (max-width: 1200px) {
              position: static;
          }
      }
      & li {
          position: relative;
          z-index: 1;
          display: table;
          float: left;
          height: 53px;
          margin-left: -1px;
          margin-right: 0;
          border: 1px solid #d0d0d0;
          @media (max-width: 1200px) {
              width: 100%;
              margin-left: 0;
              margin-bottom: -1px;
          }
          &:first-child {
              margin-left: 0;
              border-top-left-radius: 4px;
          }
          &:last-child {
              border-top-right-radius: 4px;
          }
          & h4{
              font-size: 14px;
              font-weight: 700;
              display: block;
              display: table-cell;
              padding: 15px 25px 12px;
              line-height: 1.25;
              -webkit-transition: background .3s ease 0s;
              -moz-transition: background .3s ease 0s;
              -ms-transition: background .3s ease 0s;
              -o-transition: background .3s ease 0s;
              transition: background .3s ease 0s;
              vertical-align: middle;
              text-transform: uppercase;
              color: #323232;
              font-family:  stencilFontFamily("headings-font"), Arial, Helvetica, sans-serif;
              text-decoration: none !important;
			  border:none!important;
              @media (max-width: 480px) {
                  font-size: 12px;
                  padding: 0 15px;
                  letter-spacing: 0;
              }
              &:focus {
                  outline: none;
              }
          }
          &.ActivePage {
              border: 1px solid #8200ff;
              background: #fff;
              position: relative;
              z-index: 2;
              @media (max-width: 1200px) {
                   border: 1px solid #000;
              }
          }
          &.ActivePage h4 {
			background: #8200ff;
			color: #fff;
          }
          &:hover{
              border: 1px solid #8200ff;
              border-bottom: 0;
              background: #fff;
              position: relative;
              z-index: 2;
              h4 {
                  background: #8200ff;
                  color: #fff;
              }
          }
      }
  }
  &.ExpressCheckoutBlock {
        border: 0 !important;
        box-shadow: none;
    }
  .ShippingProviderList{
    margin-top: 30px;
  }
}
