	ul.tabs {
		position: relative;
		overflow: hidden;
		margin-bottom: 5px;
		margin-top: 25px;
		border: 0;
		&:after {
			position: absolute;
			bottom:0;
			left: 0;
			width: 100%;
			z-index: 1;
			height: 3px;
			content: '';
			background: #8200ff;
			@media (max-width: 500px) {
				position: static;
			}
		}
		& li {
			height: 54px;
			line-height: 54px;
			border: 1px solid #d0d0d0;
			background: #fafafa;
			font-family:  stencilFontFamily("page-font"), Arial, Helvetica, sans-serif;
			font-size: 14px;
			font-weight: 700;
			padding: 0 19px;
			margin: 0 !important;
			margin-right: -1px !important;
			transition: background .3s ease 0s;
			vertical-align: middle;
			text-transform: capitalize;
			color: #323232;
			text-decoration: none !important;
			border-radius: 0;
			display: inline-block;
			img {
				position: relative;
				display: inline;
				left: 0px;
				top: 1px;
				margin-right: 10px;
				width: 16px;
				height: 16px;
			}
			@media (max-width: 480px) {
				font-size: 12px;
				padding: 0 15px;
				letter-spacing: 0;
			}
			&:focus {
				outline: none;
			}
			&:first-child {
				margin-left: 0;
				border-radius: 4px 0 0 0;
				-moz-border-radius: 4px 0 0 0;
			}
			&:last-child{
				border-radius:0 4px 0 0;
				-moz-border-radius: 0 4px 0 0;
			}
			@media (max-width: 500px) {
				width:auto;
				display:block;
				margin:0 0 -1px!important;
				&:last-child{
					margin-bottom:0!important;
					border-radius:0 0 4px 4px;
					-moz-border-radius:0 0 4px 4px;
				}				
				&:first-child {
					border-radius:4px 4px 0 0;
					-moz-border-radius:4px 4px 0 0;
				}
			}
			&.active{
				background: #fff;
				border: 1px solid #8200ff;
				position: relative;
				border-bottom-color: #fff;
				position: relative;
				z-index: 2;
				@media (max-width: 500px) {
				  background: #8200ff;
				  border: 0;
				  border-bottom-color: #8200ff;
				  color: #fff;
				}
			}
		}
	}	
