/* Custom Styles */
body{	
    font-family: $fontFamily-sans;
	line-height:1.55;
	background: stencilColor('color-background-body');
	font-weight:400;
    font-size: fontSize("base");
	color:stencilColor('color-textBase');
	letter-spacing:normal;
}
h1, h2, h3,h4,h5,h6{
	font-family:$fontFamily-headings;
	font-weight:700;
	color:stencilColor('color-textHeading');
	line-height:1.35;
	letter-spacing:normal;
	text-transform:capitalize;
}
h1{
	font-size:22px;
}
h2{
	font-size:20px;
}
h3{
	font-size:18px;
}
h4{
	font-size:14px;
}
h5{
	font-size:14px;
}
h6{
	font-size:12px;
}
ul{
	margin-left:15px;
}
.form-label{
	color:stencilColor('form-label-font-color');	
    font-size: fontSize("base");
}
.container_page > .container{
}
a{
	color:stencilColor('color-textBase');
}
a:hover, a:active, a:focus{
	color: stencilColor('color-textLink--hover');
}

.home-tabs {
	overflow:hidden;
	position:relative;
	@media (min-width:992px){
		padding:0 20px;
	}
	.module-heading,
	.side-module-heading{
		display:none;
	}
	.owl-carousel{
		position:static;
	}
	.owl-nav{
		position:absolute;
		top:20px;
		right:20px;
		z-index:10;
		& > div{
			position:relative!important;
			top:0!important;
			left:auto!important;
			right:auto!important;
			display:inline-block;
			text-align:center;
		}
		@media (max-width:767px){
			right:0;
		}
	}
	.hl-hometab-mobile{
		.panel-heading{
			border-top: 1px solid #e6e6e6;
			display:block;
			a{
				font: 700 16px/18px $fontFamily-headings;
				text-transform: uppercase;
				color: #000;
				border-top: 3px solid #ffdd02;
				display: inline-block;
				padding: 23px 0 20px;
				position: relative;
				top: -1px;
				&.collapsed{
					color:#7b7b7b;
				}
			}
		}
	}
}
.home-banner{
	margin:0 -15px;
	text-align:center;
	& > div{
		width:auto;
		display:inline-block;
		padding: 0 15px 15px;
		margin:0 -1.3px;
		max-width:25%;
		@media (max-width:767px){
			max-width:50%;
		}
		@media (max-width:400px){
			max-width:100%;
			padding:0 0 15px;
		}
	}
	@media (min-width:768px ) and ( max-width : 1023px){
		margin:0 -10px;
		& > div{
			padding:0 10px 10px;
		}
	}
}
.custom-block{
	border-top: 1px solid stencilColor('color-border-page');
    margin: 30px 0 0 0;
    padding: 13px 0 0;
	overflow:hidden;
	& > div {
		margin-bottom:30px;
		overflow:hidden;
		@media (min-width: 768px){
			float: left;
			padding: 0 10px 0 20px;
			margin-bottom:25px;
			width: 50%;
			&:first-child{
				padding-left:0;
			}
		}
		@media (max-width: 767px){
			padding:15px 0;
			margin:0;
			h3{
				margin-top:0;
			}
		}
	}
	h3 {
		font-size:fontSize('large');
		font-weight:700;
		font-family:$fontFamily-headings;
		text-transform: uppercase;
		margin: 25px 0 14px;
		padding: 0;
	}
	img {
		-moz-transition: all 0.8s ease 0s;
		-webkit-transition: all 0.8s ease 0s;
		transition: all 0.8s ease 0s;
		float: right;
		margin-left: 30px;
	}
}
.pace-progress{
	display:none;
}
.productCarousel{
	overflow:hidden;
	.owl-item{
		float:left;
	}
}
.page-sidebar .productCarousel{
	background:none;
}
.home-tabs #sideTopSellers {
	margin:0 -15px;
	.productCarousel{
		.prod-item{
			padding:0 15px;
		}
	}
}
.page-sidebar{
	#sideTopSellers{
		position:relative;
		.side-module-heading{
			margin-bottom:30px;
		}
		.owl-nav {
			position:static;
			box-shadow:none;
			border:none;
			& > div{
				position:absolute;
				top:16%;
				border:none;
				background:none;
				color: stencilColor('color-textBase');
				&:before{
					font-size:30px;
				}
				&.owl-prev{
					left:0;
					text-align:left;
					&:before{
						content: "\f104";
					}
				}
				&.owl-next{
					right:0;
					text-align:right;
					&:before{
						content: "\f105";
					}
				}
			}
		}
		.card-figure{
			text-align:center;
		}
		.prod-image{
			display:inline-block;
			margin:0 auto 20px;
			@media(min-width:768px) and (max-width:1023px){
				a img{
					width:100%;
				}
			}
		}
		.prod-item{
			padding:0;
			.card {
				margin:0;
			}
			.card-figure{
				& > h4.prod-name,
				& > .prod-brand{
					display:none;
				}
			}
			.star-rating{
				margin-bottom:10px;
			}
			.prod-desc{
				text-align:left;
				.prod-price{
					display:block;
					clear:both;
				}
				.free_shipping{
					text-align:left;
				}
			}
			.addto-cart{	
				a.btn{
					display:block;
				}
			}
		}
	}
}
