pages/auth/create-account:
{{inject 'passwordRequirements' settings.password_requirements}}
{{#partial "page"}}
<div class="container">
    {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
    <h1 class="page-heading hl-heading-line">{{lang 'create_account.heading' }}</h1>
    <div class="create-account">
        <div class="account account--fixed">
            <div class="account-body">
                {{#if forms.create_account.error}}
                    {{> components/common/alert-error forms.create_account.error}}
                {{/if}}
                <form action="{{urls.auth.save_new_account}}" data-create-account-form method="post" class="form">
                    <div class="form-row form-row--half">
                        {{#each forms.create_account.account_fields }}
                            {{{dynamicComponent 'components/common/forms'}}}
                        {{/each}}
                        {{#each forms.create_account.address_fields }}
                            {{{dynamicComponent 'components/common/forms'}}}
                        {{/each}}
                        {{inject 'required' (lang 'common.required')}}
                        {{inject 'state_error' (lang 'errors.state_error')}}
                    </div>
                    {{{ forms.create_account.recaptcha.markup}}}
                    <div class="form-actions">
                        <input type="submit" class="btn btn-primary btn-lg" value="{{lang 'forms.create_account.submit_value' }}" />
                    </div>
                </form>
            </div>
        </div>
    </div>
</div>
{{/partial}}
{{> layout/base}}

components/common/quick-search:
{{{ snippet "forms_search"}}}
<form class="form" action="{{urls.search}}">
    <fieldset class="form-fieldset">
        <div class="form-field">
        	<div id="search_category">
        	<select name="category" class="select_category">
		        <option value="" selected="">All</option>
		        {{#each categories}}
		            <option value="{{id}}">{{name}}</option>
		            {{#if children}}

		                {{#each children}}
		                    <option value="{{id}}">--- {{name}}</option>
		                    {{#if children}}

		                        {{#each children}}
		                            <option value="{{id}}">------ {{name}}</option>
		                            {{#if children}}

		                                {{#each children}}
		                                    {{#if children}}
		                                        <option value="{{id}}">--------- {{name}}</option>
		                                    {{else}}
		                                        <option value="{{id}}">--------- {{name}}</option>
		                                    {{/if}}
		                                {{/each}}

		                            {{/if}}
		                        {{/each}}

		                    {{/if}}
		                {{/each}}
		            {{/if}}
		        {{/each}}
		    </select> 
		    <i class="fa fa-chevron-down"></i>
		    </div>  
            <label class="is-srOnly" for="search_query">{{lang "search.quick_search.input_label"}}</label>
            <input class="form-input" data-search-quick name="search_query" id="search_query" data-error-message="{{lang 'search.error.empty_field'}}" placeholder="{{lang 'search.quick_search.input_placeholder'}}" autocomplete="off">
            <button type="submit"><svg><use xlink:href="#icon-search"></use></svg></button>
        </div>
    </fieldset>
</form>
<div class="quickSearchResultsWrap" data-prevent-quick-search-close>
    <section class="quickSearchResults" data-bind="html: results"></section>
</div>

components/common/social-links:
{{#if social_media}}
    <div class="social_media">
    <h5>Stay Connected</h5>
    <ul class="social-icons">
        {{#each social_media}}
            <li class="socialLinks-item">
                {{#if display_name '===' 'google+'}}
                    <a class="icon icon--google" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-google" /></svg>
                    </a>
                {{else}}
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
    <div class="social_icon HL-facebooklike" style="display:;">       
        <!-- FaceBook Like Button Code -->
        <div class="fb-like" data-href="https://developers.facebook.com/docs/plugins/" data-layout="button_count" data-action="like" data-show-faces="true"></div>
             <div id="fb-root"></div>
        <script>(function(d, s, id) {            
            var js, fjs = d.getElementsByTagName(s)[0];
            if (d.getElementById(id)) return;
            js = d.createElement(s); js.id = id;
            js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.0";
            fjs.parentNode.insertBefore(js, fjs);
            }(document, 'script', 'facebook-jssdk'));            
        </script>                    
    </div>
    </div>
{{/if}}

components/common/navigation-list:
{{#if children}}
<a class="navPages-action has-subMenu" href="{{url}}">
    {{name}}
</a>
<div class="navPage-subMenu" id="navPages-{{id}}" tabindex="-1">
    <ul class="navPage-subMenu-list">
        {{#each children}}
            <li class="navPage-subMenu-item {{#if children}}submenu{{/if}}">
                {{#if children}}
                    <a class="navPage-subMenu-action navPages-action has-subMenu" href="{{url}}">
                        {{name}}
                    </a>
                    <ul class="navPage-childList" id="navPages-{{id}}">
                        {{#each children}}
                        <li class="navPage-childList-item">
                            <a class="navPage-childList-action navPages-action" href="{{url}}">{{name}}</a>
                        </li>
                        {{/each}}
                    </ul>
                {{else}}
                    <a class="navPage-subMenu-action navPages-action" href="{{url}}">{{name}}</a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
</div>
{{else}}
<a class="navPages-action" href="{{url}}">{{name}}</a>
{{/if}}

components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
<li id="currency-converter">
    <label>{{lang 'currency.label'}}</label>
    <a class="navUser-action has-dropdown" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false"><span>{{{currency_selector.active_currency_flag}}} {{currency_selector.active_currency_name}}</span><i class="fa fa-chevron-down"></i></a>
    <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
        {{#each currency_selector.currencies}}
        <li class="dropdown-menu-item">
            <a href="{{{switch_url}}}">
                {{#if is_active}}
                {{{flag}}} <strong>{{name}}</strong>
                {{else}}
                {{{flag}}} {{name}}
                {{/if}}
            </a>
        </li>
        {{/each}}
    </ul>
</li>
{{/if}}
